package com.mzbr.videoencodingservice.util;import java.nio.file.Path;import org.springframework.beans.factory.annotation.Value;import org.springframework.stereotype.Component;import lombok.RequiredArgsConstructor;import software.amazon.awssdk.core.sync.RequestBody;import software.amazon.awssdk.services.s3.S3Client;import software.amazon.awssdk.services.s3.model.PutObjectRequest;@Component@RequiredArgsConstructorpublic class S3Util {	private final S3Client s3Client;	@Value("${cloud.aws.s3.bucket}")	private String BUCKET_NAME;	public void uploadFile(String filepath, String fileUploadPath) {		PutObjectRequest putObjectRequest = PutObjectRequest.builder()			.bucket(BUCKET_NAME)			.key(fileUploadPath)			.build();		s3Client.putObject(putObjectRequest, RequestBody.fromFile(Path.of(filepath).toFile()));	}}